sys_script_client.script:
function onLoad() {
    if (!g_form.isNewRecord()) {
        return;
    }
    var ga = new GlideAjax('PurchaseAndDeliveryDate');
    ga.addParam('sysparm_name', 'getDate');
    // 	ga.addParam('sysparm_purchased_date',g_form.getValue('date_purchased'));
    //     ga.getXMLAnswer(responseMethod);
    ga.getXML(responseMethod);
}

function responseMethod(response) {
    var result = response.responseXML.documentElement.getAttribute("answer");
    var resultData = JSON.parse(result);
    //     g_form.addInfoMessage("aaasss" + resultData);
    g_form.setDisplay('delivery_date', false);
    g_form.setValue('date_purchased', resultData.purchase);
}
