sys_script_include.script:
var PurchaseAndDeliveryDate = Class.create();
PurchaseAndDeliveryDate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getDate: function() {
        var purchaseDate = new GlideDateTime();
        var result = {
            purchase: purchaseDate.getDate().toString()
        };
        return JSON.stringify(result);
    },
	getDeliveryDate: function() {
// 					gs.addInfoMessage("heee");
// 		var deliveryDate = new GlideDateTime(this.getParameter('sysparm_purchased_date'));
		var deliveryDate = new GlideDateTime();
        deliveryDate.addDaysLocalTime(5);
// 		gs.addInfoMessage("sys_id"+this.getParameter('sysparm_sys_id'));
		var gr = new GlideRecord('x_960589_collect_0_collection_table');
		gr.get(this.getParameter('sysparm_sys_id'));
		gr.query();
// 		gs.addInfoMessage("count"+gr.getRowCount());
		gs.info("count"+gr.getRowCount()+"sys_id"+this.getParameter('sysparm_sys_id')+"date"+deliveryDate.getDate().toString());
		if(gr.next()){
		gr.setValue('delivery_date',deliveryDate.getDate().toString());
		gr.update();
		}

		return "success";

	},
    type: 'PurchaseAndDeliveryDate'
});
