record.addQuery('delivery_date',">=",date);
record.addQuery('delivery_date','<=',date2.getDate());
record.query();
// gs.info("count"+record.getRowCount());
while(record.next()){
// 	gs.info("The record is "+ record.number);
	gs.eventQueue('x_960589_collect_0.DeliveryDueinOneday',record,record.number,gs.getUserName());
}
